<script>

    import hierarchy from "../assets/hierarchy.json"
    import clientOrganization from '../assets/clientOrganization'
    import CustomMultiselect from './CustomMultiselect.vue'
    import SummaryBar from './summary.vue'

    export default {
        name: "Actionsbutton",
        components: {
            CustomMultiselect,
            SummaryBar,
        },
        data() {
            
            return {
                transmiter: [
                    { "id": "transmiterOneFrameEveryDay", "name": "Au moins  trame quot.", "color": "#79E480" },
                    { "id": "atLeast1Frame", "name": "Au moins 1 trame", "color": "#FFA34E" },
                    { "id": "NoFrame", "name": "Aucune trame", "color": "#EE685E" },
                ],
                concentrator: [
                    { "id": "Variation", "name": "Variations", "color": "#79E480" },
                    { "id": "Fonctionnal", "name": "En Fonction", "color": "#FFA34E" },
                    { "id": "Default", "name": "En défault", "color": "#EE685E" },
                ],
                clientOptions: [
                    { "option": hierarchy.Children, "value": [], "id": 0, "name": "Client"},
                ],
                dateSelected: "2010-01-01",
                periodSelected: "10",
                numberOfMultiselect: 0,
                clientPathSelected: [],
                selected: [],
                allSelected: false,
                allSelectedTransmiter: false,
                allSelectedConcentrator: false,
                selectedIdTransmiter: [],
                selectedIdConcentrator: [],
                selectedId: [],
                FilterisVisible: false,
                ContractOptionisVisible: false
            }
        },
        methods: {
            onMultiselectOptionClick (val, index) {
                let multiselectOrganization = [];
                if (index == 0) {
                    this.clientOptions = this.clientOptions.filter(el => {
                    return el.id <= index
                })}
               this.clientOptions[index].value = this.getValue(val)
                if (index == 0) {
                    val.forEach(el => {
                        clientOrganization.forEach( element => {
                            if(el.Label == element.Id)
                            multiselectOrganization.push(element)
                        })
                    });
                    this.numberOfMultiselect = this.getNumberOfMultiselect(multiselectOrganization)
                for(let nb = 0 ; nb < this.numberOfMultiselect; nb += 1) {   
                this.clientOptions.push({"option": [], "value": [], "id":  (nb+1), "name": this.getNameByTier(nb, multiselectOrganization)});
                }}
                this.setValueIfOnlyOneChildren(val, index);
                if(index < this.numberOfMultiselect) {
                    this.clientPathSelected[index] = val
                   
                    for (let nb = index+1; nb <= this.numberOfMultiselect; nb += 1) {
                        this.clientOptions[nb].option = this.getFirstOptions(this.clientOptions, nb)
                    }
                }
                this.clientPathSelected[index] = this.getPath(val)
                this.clientPathSelected = this.cleanPath(this.clientPathSelected, index)
            },
            getValue(selectPath) {
                return selectPath.flatMap(el => el.Path)
            },
            getFirstOptions(client, index) {
                if (client[index-1].value.length === 0)
                    return client[index-1].option.flatMap(el => el.Children)
                 let clientChildren = client[index-1].value.flatMap(singleValue => 
                    client[index-1].option.filter(objOption => {
                        return objOption.Path === singleValue 
                    })
                )
                return clientChildren.flatMap(el => el.Children)
            },
            getNumberOfMultiselect(val) {
            let numbermax = 0;
            val.forEach(el => {
                if(numbermax < el.LevelCount)
                numbermax = el.LevelCount
            })
            return numbermax
            },
            getNameByTier(tier, value) {
            let name = [];
            value.forEach(el => {
                if (!name.includes(el.Levels[tier])) {
                name.push(el.Levels[tier])
                }
            })
            name = name.filter(el => el != null)
            name = name.join(' / ')
            return name
            },
            getPath(value) {
                return value.flatMap(el => el.Path)
            },
            getLastPathPerTree(arrayOfPath) {
                let result = arrayOfPath[0];

                for(let nb = 1; nb < arrayOfPath.length; nb += 1) {
                    let temp = result
                    result = result.map(element => {
                        return arrayOfPath[nb].filter(el => el.includes(element))
                    })
                    result = result.flatMap((el, index) => {
                        if (el.length == 0) {
                            return el = temp[index]
                        }
                        else
                        return el
                    })
                }
                return result
            },
            cleanPath(clientPath, index) {
                for (let nb = 0; nb <= this.numberOfMultiselect; nb += 1) {
                    if (nb > index)
                    clientPath[nb] = []
                }
                return clientPath
            },
            setValueIfOnlyOneChildren(selectPath, index) {
                if(selectPath.flatMap(el => el.Children).length === 1) {
                    this.clientOptions[index+1].value = selectPath.flatMap(el => {
                        return el.Children.flatMap(element => element.Path)
                    })
                }
            },
            selectAllTransmiter: function () {
                this.selectedIdTransmiter = [];

                if (!this.allSelectedTransmiter) {
                    for (this.element in this.transmiter) {
                        this.selectedIdTransmiter.push(this.transmiter[this.element].id.toString());
                    }
                }
            },
            selectTransmiter: function () {
                this.allSelectedTransmiter = false;
            },

            selectAllConcentrator: function () {
                this.selectedIdConcentrator = [];

                if (!this.allSelectedConcentrator) {
                    for (this.element in this.concentrator) {
                        this.selectedIdConcentrator.push(this.concentrator[this.element].id.toString());
                    }
                }
            },
            selectConcentrator: function () {
                this.allSelectedConcentrator = false;
            },
            SendInformation() {
                var emitInformations = {
                    ajoutElem: function ajoutElem(elem) {
                        [].push.call(this, elem);
                    }
                };
                emitInformations.ajoutElem(this.getLastPathPerTree(this.clientPathSelected))
                emitInformations.ajoutElem(this.selectedIdTransmiter.concat(this.selectedIdConcentrator));
                emitInformations.ajoutElem(this.dateSelected);
                emitInformations.ajoutElem(this.periodSelected);
                
                console.log(emitInformations)
            },
            resetInformation() {
                this.allSelectedTransmiter = false;
                this.allSelectedConcentrator = false;
                this.selectedIdTransmiter = [];
                this.selectedIdConcentrator = [];
                this.clientOptions = [{ "option": hierarchy.Children, "id": 0 }]
                this.periodSelected='10';
                this.dateSelected='2010-01-01';
            }
        }
    }
</script>

